INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC001', 'Hardware Documentation printed', 'Description of CSIRAC hardware prior to transfer to Melbourne', NULL, 'c1949-1955', 9, 3, 'Description of CSIRAC hardware prior to transfer to Melbourne. Copied from Ron Bowles'' collection 2006.\r\nThere were several copies of this document in Melbourne. This was the only one located for the archive.\r\nPhotocopied and bound. Also scanned for CD.\r\n\r\nOriginal material in Museum Victoria.', 'db_img/CSIRAC hardware description 1955.pdf');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC002', 'Hardware and Software Documentation CD', 'Various Hardware Software etc', NULL, '1947-2005', 1, 3, 'CD of selected hardware and software documentation prepared by John Spencer.\r\nContent details of all the folders on the disc follows:\r\nThe folders on this CD are:\r\nIndex.txt         this file \r\nDrawings         contains the CSIRAC drawings\r\nEmulator         contains DOS and Windows versions of the CSIRAC emulators\r\nExamples        contains a few examples to use with the CSIRAC emulators\r\nFilms                      contains films related to CSIRAC \r\nGames\t                   contains the CSIRAC games\r\nInterprogram      contains the two operative Interprograms T754 and T755, \r\n                                 together with F. Hirst''s 5-hole compiled  Interprogram\r\nLibrary                   contains the CSIRAC program library\r\nManuals                contains digital copies of CSIRAC manuals\r\nMusic                     contains files relating to the Sydney and Melbourne CSIRAC  music\r\nPhotos                   contains photographs related to CSIRAC\r\nSolar Tables         contains  J. Spencer''s programs for Solar Tables (Project  CBR251)\r\nSource                   contains the source code (TurboPascal6 or Delphi4) for the  .exe files in other folders\r\nTape Handling    contains programs for conversion of hex files to .cvt files, and for creating .tsp files \r\n                                 from  .cvt  files\r\nTiming                   contains a document by R. Bowles describing CSIRAC timing \r\nNote.txt               contains a note about read-only attribute of CD files\r\nReadme.txt         contains general notes about file viewing  \r\nView ASCII file.com\t file viewer for ASCII files (DOS Edit renamed)\r\n\r\nFor further details consult the Index.txt files in individual folders.\r\nThe CD occupies about 306 MB \r\nJune 2010\r\nSuperceded by later versions up to 2012 (comment added 20/03/12)', 'db_img/CSIRAC002.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC003', 'Drawings', 'Printed copies of scanned drawings reduced to A4', NULL, '1948-1961', 1, 2, 'Printed copies of scanned drawings reduced to A4. \r\nAlso on CD see CSIRAC002.\r\nThese include block,  logic, timing, circuit  diagrams and mechanical drawings.\r\nOriginal drawings held at Museum Victoria.  Note: not all drawings were scanned.', 'db_img/CSIRAC003.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC004', 'Drawings', 'Selected copies in original size.', NULL, '1948-1961', 1, 2, 'These are selected copies in original size B and C.  Note: original drawings at Museum Victoria\r\nB18600 Arithmetic organ A03\r\nB22578 Memory Input Gates\r\nB23209 Arithmetic organ of computor (on tracing paper,paper x2sizes)\r\nSKE5182 Interpreter (2 copies)\r\nC23125 Arithmetic Organ A04\r\nSequence Unit Microwave Forms\r\nMiscellaneous Chassis drawings x8\r\n(Rolled in short tube)', '');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC005', 'Chassis/Drum Photographs', NULL, NULL, '1998', 1, 1, '13 x photographs/negatives of CSIRAC chassis positions in the cabinets and the drum. Taken in 1998.\r\nThese were taken by Jurij Semkiw & Ron Bowles at Scienceworks prior to display at Melbourne Museum.', '');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC006', 'Programming Manual', 'For the Automatic Electronic Computer CSIRAC', 'Computation Laboratory Staff', 'December 1956', 1, 3, 'First edition of the CSIRAC Programming Manual (grey cover).\r\nThis production by the staff of the Computation Laboratory is based upon papers by T Pearcey and GW Hill. \r\nSee CSIRAC031 for digital CD copy.\r\n\r\nWhen the CSIR Mk1 arrived at the Computation Laboratory in 1955, programming courses were initiated for university and CSIRO staff.  Frank Hirst called upon the existing programming manuals to develop a manual that incorporated a brief description of the computer and its operation, the instruction set and other information to assist prospective users.  The 1956 version contains detailed material (used for instance in the re-construction of the music) which is absent in later versions. The 1959 edition was an updated and  simplified version of the earlier manual.\r\nNote:1956 versions such as this, had a grey cover. \r\nLater versions of which more have survived had a buff/gold cover.\r\nAdditional copies - see Literature CScL0544\r\nPhotocopy master used for scanning document', 'db_img/CSIRAC Programming Manual1956.pdf');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC007', 'Paper Tapes 12 hole', NULL, NULL, '1950-1964', 1, 3, '12-hole paper tapes of programs T789, T916, T781, T504 copy, T844, T504S, 2 unlabelled 12 hole tapes (total of 8 tapes)\r\nInformation on program tapes available in CSIRAC Program Library on documentation CD CSIRAC003.', '');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC008', 'CSIRAC correspondence', NULL, NULL, '1958-1962', 1, 2, 'Letter from JJ Masur & Co Pty Ltd dated September 9, 1958 advising now representing Electronic Associates Ind.\r\nQuotation No 367 from Radio Parts Pty Ltd dated September 12, 1962 for transistors.  These were probably potential components for the electronic (transistorized) circuitry which was \tdesigned  and built by Jurij Semkiw to double the capacity of the magnetic disk/drum backing store', 'db_img/CSIRAC008.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC009', 'Film 16mm, U-Matic, Betacam', 'CSIRAC Publicity Material', NULL, '1964', 1, 3, 'CSIRO Film Unit \"Computer CSIRAC\" 1964 16mm film (copy 7) 16mins.\r\nFilmed at Melbourne University Computation Laboratory prior to the decommissioning of CSIRAC computer. \r\nContains images of Trevor Pearcey, Frank Hirst, Kay Thorne and Jurij Semkiw.\r\nThis is the most complete film record of the operation of CSIRAC and of the Computation Laboratory. Extracts of it have been used widely in documentaries and the background sound of the Laboratory was used by Paul Doornbusch in reconstructing the CSIRAC music.\r\n(In plastic bag)', 'db_img/CSIRAC009.jpg');
