INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC021', 'IRE (Printed  Paper)', '\"A Magnetic Drum Digital Storage System\"', 'B.F.C. Cooper', '1953', 9, 1, 'Photocopy: CSIRO, Proceedings of the IRE July 1953. \r\n\"A Magnetic Drum Digital Storage System\" by B.F.C. Cooper.\r\n\r\nSeveral magnetic storage systems were  developed for CSIR MK1/CSIRAC. The early versions were cylinders \thence the term \"drum\". The final version was a disk but the name \"drum\" was retained.\r\n\r\n\t\tRon Bowles collection.', 'db_img/Deum text Brian Cooper.pdf');
