INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC027', 'CSIRAC Machine Optimization and Timing', NULL, NULL, '1962-2002', 9, 3, 'These papers provide an insight into the detailed timing of CSIRAC and the actions undertaken to improve the speed of execution of programs during the working life of the computer.  They also provide details of some of the idiosyncracies of the computer which were relevant to programmers.\r\nRon Bowles Collection', 'db_img/CSIRAC NOTES MARCH 1962.pdf;CSIRAC NOTES OCTOBER1962.pdf');
