INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC029', 'The Last of the First', 'CSIRAC: Australia''s First Computer', 'Doug McCann & Peter Thorne', '2000', 1, 3, 'ISBN 0734020244\r\nThis book, edited by McCann and Thorne, was based on presentations made at the conference held in the Department of Computer Science in 1996, to mark the 40th anniversary of the re-commisioning of the CSIR Mk1/CSIRAC at the University of Melbourne. This was the first computer in Victoria and thus the introduction of electronic computing to the state. At that time CSIRAC was the only operational computer in Australia.\r\nThe conference papers were augmented by oral histories and contributions by McCann and Thorne,  CSIRO staff member and computer historian, John Deane and Ron Bowles. The publication used almost all then known, surviving, photographs of the computer and its users. Preparation of the book for printing (including the indexing) was undertaken by Judith Hughes.', 'db_img/BOOK COVER.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC030', 'Computer Music Journal', 'Volume 28, Number 1', 'Paul Doornbusch', '2004', 1, NULL, 'ISSN 0148-9267\r\nPublisher: MIT Press\r\nThe History of Electroacoustic Music\r\nComputer Sound Synthesis in 1951: The Music of CSIRAC\r\nWritten by Paul Doornbusch\r\nPages 10-25', 'db_img/001.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC031', 'Programming Manual 1956 - CD', '\"CSIRAC Programming Manual for Automatic Electronic Computer\"', 'Computation Laboratory Staff', 'December 1956', 1, 3, 'First edition of the CSIRAC Programming Manual (grey cover) in digital CD format.\r\nThis production by the staff of the Computation Laboratory is based upon papers by T Pearcey and GW Hill.\r\nSee CSIRAC006 for original.\r\n\r\nNote: early (1956) versions had a grey cover. \r\nLater (1959) versions, of which more have survived, had a buff/gold cover.', 'db_img/CSIRAC031.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC032', 'CSIRAC Interviews 2xVHS tapes', 'Tapes 1, 2, 3 and 4,5,6.', NULL, '1996', 14, 2, 'Copies of Steve Pass interviews with Trevor Pearcey, Ron Bowles, Jurij Semkiw and Peter Thorne. Material recorded for Steve Pass documentary. Original material held by Steven Pass who holds copyright.', 'db_img/CSIRAC032a.jpg');
INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC033', 'CSIRAC Australia''s First Computer', NULL, 'John Deane', '1997', 1, NULL, 'Publisher: Australian Computer Museum Society, Inc.\r\nSigned by author. \r\nIn this booklet completed in 1997, John Deane reconstructed the workings of CSIRAC from documents.  This  was a remarkable achievement. Some details were later amended in light of the first hand experience of Ron Bowles and Jurij Semkiw.   (Ron Bowles provided a list of corrections which form part of this collection (see CSIRAC034).   Parts of John''s work were later incorporated (with his permission) into \"The Last of the First - CSIRAC: Australia''s First Computer\".', 'db_img/002.jpg');
