INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC042', 'CSIRAC Various', NULL, NULL, '1956-2001', 1, 1, 'Unpublished Paper - CSIRAC Timing - Ron Bowles 2001\r\nCSIRAC Talk - Terry Holden 1996 - Presented at CSIRAC Conference June 13-14, 1996\r\nCSIRAC Conference June 13-14, 1996 - Program\r\nPhotocopy - CSIRO, Building Research - Current Status of CSIRAC Projects - 8/7/1959\r\nCSIRAC Notes for Programmers - 24/10/1962\r\nRoutine Specification Sheet - T510 - The Runge Kutta integration April 1959\r\nPaper - Runge-Kutta Process - RP Harris - Australian Computer Conference - 1969\r\nMemory Systems - Frank Hirst c1957\r\nProcedure for Spraying Magnetic Disc for Electronic Computer - E Holmes c1956\r\nCSIRAC Library - Copies of Library Rountines - TS Holden 1960\r\nRoutine Specification Sheets - T113, T044.1, T044.2, T044.3, T044.4 - John Spencer c1962 \r\n2 x Diaries - CSIRAC Details - Ron Bowles c1999\r\nCSIRO Information Sheet - CSIRAC Mk1 - John Deane 1981\r\nMelbourne University Computer Advisory Group - \"The Computer Today\" 1965\r\nVCS Bulletin February 1965 - Farewell to CSIRAC by Frank Hirst\r\nVCS Bulletin November 1991 - History on the Move - CSIRAC move from Caulfield to MV Spotswood\r\nVCS Bulletin August 1992 - Dr T Pearcey, New ACS Honorary Life Member\r\nCSIRAC Command Codes - August 1952 - Sydney\r\nAscent Technology Magazine - November 1996 - The world''s oldest computer\r\nEngineers Australia - November 1989 - Computers in Engineering \r\nUni News - June 1996 - A computing ''monster meets a modern ''mouse''', '');
