INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC044', 'CSIRAC Archival Tape Reading Project CDx2', '\"Overcoming Obsolesence\"', NULL, '2009', 17, 2, '1. Museum Victoria recorded a videoptape entitled \"Overcoming Obsolesence\".  This shows Jurij Semkiw and John Spencer explaining the equipment developed and how it worked to enable 12-hole CSIRAC tapes to be read in 1999.  There are three versions on CD - only one in high definition.\r\n2. Recorded components for the making of the above videotape \"Overcoming Obsolesence\" including logical diagram of the tape reading system.  John Horvath''s program for the PC used to operate the modified Apple printer.', '');
