INSERT INTO `CSIRAC` (`CSIRACID`, `CSIRACObjectName`, `CSIRACObjectNameQualifier`, `Authors`, `CSIRACDate`, `Provenance`, `Significance`, `CSIRACComments`, `ImageLocation`) VALUES ('CSIRAC054', 'Computation Laboratory Door', NULL, NULL, NULL, 1, 3, 'This door was originally installed at the entrance to the Computation Laboratory in the Natural Philosophy (Old Physics) building. \r\nThe Laboratory was established to house AustraliaÃ¢â‚¬â„¢s first digital electronic computer CSIRAC which was designed and built in Sydney and transferred to the University of Melbourne in 1955. \r\nCSIRAC was the first computer installed in an Australian university. \r\nThe Computation Laboratory was the predecessor department of the Department of Computing and Information Systems. \r\nIt was the first such department in an Australian university and one of the first in the world.', 'db_img/Computation Laboratory original door col ref 135.jpg;db_img/The Door.ppt');
