INSERT INTO `Hardware` (`HardwareID`, `HardwareName`, `HardwareNameQualifier`, `HardwareManufacturer`, `HardwareModel`, `HardwareSerialNumber`, `HdwManufDateQualifier`, `HardwareManufactureDate`, `HardwareSignificance`, `HardwareComments`, `ImageLocation`) VALUES ('Hrd0018', 'Combitron S (complete)', 'with Dilector, manual & tape', 'Diehl', NULL, '30312098 Combitron/1346 Dilector', '1969', '1968', 2, 'Donated by Ian Wright, Seatech Consultants Pty Ltd.\r\nProgrammable calculator with printer output and tape reader and 3xprogram tapes. \r\nManufactured in Germany.\r\nDocumentation includes a programming manual, operating instructions, samples of output from the printer and handwritten programming notes.  Also included are the letters documenting the donation in September 1992.\r\nAt the right the Diehl Dilector, a paper tape reader to enter numbers and\r\nprograms. It is coupled to the keyboard and sends the corresponding keycode\r\naccording to the perforation to the computer.', 'db_img/Hrd0018.1.JPG;db_img/Hrd0018.2.JPG;db_img/Hrd0018.JPG');
