INSERT INTO `Hardware` (`HardwareID`, `HardwareName`, `HardwareNameQualifier`, `HardwareManufacturer`, `HardwareModel`, `HardwareSerialNumber`, `HdwManufDateQualifier`, `HardwareManufactureDate`, `HardwareSignificance`, `HardwareComments`, `ImageLocation`) VALUES ('Hrd0039', 'HP35', 'Hewlett Packard', 'Hewlett Packard', 'HP35', NULL, NULL, '1973', 2, 'Early calculator with charger. With purchase documents on box and manual.\r\nPurchased by the Department for Dr Rex Harris and used by him over the years. He then donated it for archiving.', 'db_img/Hrd0039.1.JPG;db_img/Hrd0039.4.JPG');
INSERT INTO `Hardware` (`HardwareID`, `HardwareName`, `HardwareNameQualifier`, `HardwareManufacturer`, `HardwareModel`, `HardwareSerialNumber`, `HdwManufDateQualifier`, `HardwareManufactureDate`, `HardwareSignificance`, `HardwareComments`, `ImageLocation`) VALUES ('Hrd0040', 'Acoustic Coupler', NULL, 'Moore Reed', 'TC301', '74178', NULL, 'c1970s', 2, 'Communications system that predated modems. Post Master General (PMG)  -later Telecom Australia -  regulations prohibited electrical connection to their network except for type approved devices. To overcome this, acoustic couplers were used which converted electrical signals into acoustic tones (and acoustic tones into electrical signals) to be received  or transmitted by a conventional telephone handset. They were slow and notoriously error prone. The collection has two of these devices.', 'db_img/Hrd0040.2.jpg');
