INSERT INTO `Hardware` (`HardwareID`, `HardwareName`, `HardwareNameQualifier`, `HardwareManufacturer`, `HardwareModel`, `HardwareSerialNumber`, `HdwManufDateQualifier`, `HardwareManufactureDate`, `HardwareSignificance`, `HardwareComments`, `ImageLocation`) VALUES ('Hrd0051', 'Arcturus hardware modules', NULL, 'University of Sydney', NULL, NULL, NULL, '1960-1966', 3, 'Arcturus was a transistorised computer built at the University of Sydney between 1960 and 1966.\r\nThese modules were donated to the collection when Dr Doug McCann visited the University of Sydney in mid-1990s.\r\nThey are significant because they are remanants of a unique, Australian designed and constructed, computer.\r\nIt is notable that the cards are not printed circuits boards. Instead, they are mounting cards through which the components are inserted, the connections being made by soldered wires.\r\n\r\nThis object is considered significant because itwas  part of an early computer designed and constructed in Australia at the time when transistors began to replace vacuum tubes.', 'db_img/Hrd0051.1.JPG;db_img/Hrd0051.JPG');
