INSERT INTO `Literature` (`LitID`, `HrdID`, `LitPublDate`, `LitTitleQual`, `LitTitle`, `Authors`, `LitComments`, `Publisher`, `LitSignificance`) VALUES ('CScL0412', NULL, '1953c', NULL, 'D.E.U.C.E. Programming Manual', NULL, 'DEUCE was a descendant of ACE.  ACE was the machine developed at the UK National Physical Laboratory in accordance with Alan Turing''s design.  The first computer at the University of Technology, Sydney (now UNSW) was a DEUCE renamed UTECOM.', 'The English Electric Company Limited', 'High');
