INSERT INTO `Literature` (`LitID`, `HrdID`, `LitPublDate`, `LitTitleQual`, `LitTitle`, `Authors`, `LitComments`, `Publisher`, `LitSignificance`) VALUES ('CScL0425', NULL, '1978-1982c', 'Model Train Project', 'Departmental Project', NULL, 'The Model Train project was developed to provide a teaching tool for students studying Real-Time Systems.  The train system was notable for the complexity of the track layout but more importantly for the technology used to control the trains.  This used digital signalling superimposed on the track power supply.  The system enabled individual control of up to seven locomotives even if they were travelling on the same section of track.  Much of the concept and design of the controller system was due to Jurij Semkiw.', NULL, 'Med');
