INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph133', 'Babbage Difference Engine No. 1', NULL, NULL, 'Powerhouse Museum', 'db_img/Babbage Difference Engine spec bw ref 133.jpg;db_img/Babbage Difference Engine spec col ref 133.jpg', 'Specimen piece of the Babbage Difference Engine No. 1.  Owned by the Powerhouse Museum, Sydney.  One of only six demonstration pieces assembled by Charles Babbage''s son Henry, after his father''s death, utilising parts manufactured for the original Difference Engine Project.\r\nNote: Two photographs: one colour/one black & white.\r\n*Published in \"The Last of the First - CSIRAC Australia''s First Computer\" copyright 2000');
