INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph179', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 179.jpg', 'Roy Muncey from CSIRO Division of Building Research at console with 12 hole tape reader to his left and cabinets in front. Editing plate sitting on console to his left.  Test unit at far right. Taken at night 17 June, 1960.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph180', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 180.jpg', ' Roy Muncey from CSIRO Division of Building Research at console with teletype printer to his right, cathode ray tubes, switch panel and 12 & 5 hole readers to his left. Roy is holding up 5 hole tape. A box of paper tape programs is sitting on top of the cathode ray tubes. Taken at night 17 June, 1960');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph181', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 181.jpg', 'Roy Muncey from CSIRO Division of Building Research at console using switch panel with cathode ray tubes and teletype printer to his right.  A box of paper tape programs is sitting on top of the cathode ray tubes and 5 hole paper tape is on cosole. Test unit at far right. Taken at night 17 June, 1960.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph182', 'Team from CSIRO Division of Building research using CSIRAC.', '1960', ' L-r Roy Muncey, Bill Davern', 'CSIRO', 'db_img/Copy of CSIRO DBR''s l-r Roy Muncey, Bill .jpg', 'FIX THIS???Roy Muncey from CSIRO Division of Building Research at console using switch panel with cathode ray tubes and teletype printer to his right.  A box of paper tape programs is sitting on top of the cathode ray tubes and 5 hole paper tape is on console. Test unit at far right. Bill Davern standing. Taken at night 17 June, 1960.\r\nRoy Muncey from CSIRO Division of Building Research at console with right hand touching cathode ray tubes.  12 hole & 5 hole tape punch to his right. Tape winder to far left with 5 hole  & 12 hole reader. Flexowriter to left of cabinets. Taken at night 17 June, 1960.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph183', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 183.jpg', 'Roy Muncey from CSIRO Division of Building Research at console with right hand touching cathode ray tubes.  12 hole & 5 hole tape punch to his right. Tape winder to far left with 5 hole  & 12 hole reader. Flexowriter to left of cabinets. Taken at night 17 June, 1960.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph184', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 184.jpg', 'Neg 9: Roy Muncey from CSIRO Division of Building Research at console with left hand on switch panel. 12 hole & 5 hole tape punch to his right. Tape winder to far left with 5 hole  & 12 hole reader. Flexowriter to left of cabinets. Taken at night 17/6/1960.  See also Neg 8, 10 & 12 . See Neg 7 for similar view.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph185', 'Roy Muncey wih CSIRAC console', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 185.jpg', 'Neg 11: Roy Muncey from CSIRO Division of Building Research at console with pencil in his hands, routine booklet and 5 hole paper tape in front of him. Switch panel to his left, cathode ray tubes with box of paper tape programs on top and teletype printer to his right. Flexowriter is to left of cabinets. Taken at night 17/6/1960');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph186', 'Roy Muncey wih CSIRAC Flexowriter', '1960', 'Roy Muncey', 'CSIRO', 'db_img/CSIRO DBR''s Roy Muncey 1960 ref 186.jpg', 'Neg 14: Roy Muncey from CSIRO Division of Building Research at Friden flexowriter with 5 hole tape being read on the left and a printout being generated on main part of flexowriter. Lighter print than Neg 13. Taken at night 17/6/1960.  See also Neg 13, 15 & 16.');
INSERT INTO `Photographs - CSIRAC` (`RefNo`, `Title`, `Date`, `Personel`, `Copyright`, `ImageLocation`, `Notes`) VALUES ('csiracph187', 'Don Beresford at console', '1960', 'Don Beresford', 'CSIRO', 'db_img/CSIRO DBR''s Don Beresford 1960 ref 187.jpg', 'Roy Muncey from CSIRO Division of Building Research at console with right hand touching cathode ray tubes.  12 hole & 5 hole tape punch to his right. Tape winder to far left with 5 hole  & 12 hole reader. Flexowriter to left of cabinets. Taken at night 17 June, 1960. Don Beresford  from CSIRO Division of Building Research at console. He is holding up 12 hole paper tape. 5 hole paper tape is sitting on the console to his left. Don''s face is blurry. The Building Research custom made hand punch for 12 hole tape, and editing plate, is on far left of console. Routine sheets are sitting in front of Don.  Switch panel is to left of console, cathode ray tubes in middle and teletype printer at right hand end of console. Test unit to right of cabinets. 5 hole punch to far right of Don. Taken at night 17/6/1960');
